import { Component, OnInit } from "@angular/core";
import { UntypedFormBuilder, UntypedFormGroup } from "@angular/forms";

@Component({
  selector: "app-buttons",
  templateUrl: "./buttons.component.html",
  styleUrls: ["./buttons.component.scss"],
})
export class ButtonsComponent implements OnInit {
  public checkboxGroupForm: UntypedFormGroup;
  public radioGroupForm: UntypedFormGroup | any;

  constructor(private formBuilder: UntypedFormBuilder) {}

  ngOnInit() {
    this.checkboxGroupForm = this.formBuilder.group({
      left: true,
      middle: false,
      right: false,
    });
    this.radioGroupForm = this.formBuilder.group({
      model: 1,
    });
  }

  model = {
    left: true,
    middle: false,
    right: false,
  };

  modelRadio = 1;
}
<app-breadcrumb [title]="'Buttons'" [items]="['Bonus UI', 'Base']" [active_item]="'Buttons'"></app-breadcrumb>
<!-- Container-fluid Ends -->
<div class="container-fluid">
  <div class="row">
    <div class="col-sm-12 col-xl-6">
      <div class="card">
        <div class="card-header">
          <h5>Checkbox buttons</h5>
        </div>
        <div class="card-body">
          <div class="btn-group btn-group-toggle">
            <label class="btn-primary" ngbButtonLabel> <input type="checkbox" ngbButton [(ngModel)]="model.left" /> Left (pre-checked) </label>
            <label class="btn-primary" ngbButtonLabel> <input type="checkbox" ngbButton [(ngModel)]="model.middle" /> Middle </label>
            <label class="btn-primary" ngbButtonLabel> <input type="checkbox" ngbButton [(ngModel)]="model.right" /> Right </label>
          </div>
          <hr />
          <pre class="mb-0">{{ model | json }}</pre>
        </div>
      </div>
    </div>
    <div class="col-sm-12 col-xl-6">
      <div class="card">
        <div class="card-header">
          <h5>Checkbox buttons (Reactive Forms)</h5>
        </div>
        <div class="card-body">
          <form [formGroup]="checkboxGroupForm">
            <div class="btn-group btn-group-toggle">
              <label class="btn-primary" ngbButtonLabel> <input type="checkbox" formControlName="left" ngbButton /> Left (pre-checked) </label>
              <label class="btn-primary" ngbButtonLabel> <input type="checkbox" formControlName="middle" ngbButton /> Middle </label>
              <label class="btn-primary" ngbButtonLabel> <input type="checkbox" formControlName="right" ngbButton /> Right </label>
            </div>
          </form>
          <hr />
          <pre class="mb-0">{{ checkboxGroupForm.value | json }}</pre>
        </div>
      </div>
    </div>
  </div>
</div>
