import { Component, OnInit } from '@angular/core';
declare var require
const Swal = require('sweetalert2')

@Component({
  selector: 'app-sweet-alert2',
  templateUrl: './sweet-alert2.component.html',
  styleUrls: ['./sweet-alert2.component.scss']
})
export class SweetAlert2Component implements OnInit {
  constructor() { }

  ngOnInit(): void {
  }
  // Basic Alert
  basicAlert() {
    Swal.fire('Any fool can use a computer')
  }

  // Alert Title
  withTitle() {
    Swal.fire(
      'The Internet?',
      'That thing is still around?',
      'question'
    )
  }

  // Alert with Image
  withImage() {
    Swal.fire({ imageUrl: 'assets/images/logo/logo.png', imageAlt: 'A Cuba Logo' })
  }

  // A modal with a title, an error icon, a text, and a footer
  error() {
    Swal.fire({
      type: 'error',
      title: 'Oops...',
      text: 'Something went wrong!',
      footer: '<a href>Why do I have this issue?</a>'
    })
  }

  // A warning
  warning() {
    Swal.fire({
      type: 'warning',
      title: 'Warning',
      text: 'You clicked the button!',
      showConfirmButton: true,
    });
  }

  // A warning
  success() {
    Swal.fire({
      type: 'success',
      title: 'Success',
      text: 'You clicked the button!',
      showConfirmButton: true,
    });
  }

  // Info
  info() {
    Swal.fire({
      type: 'info',
      title: 'Info',
      text: 'You clicked the button!',
      showConfirmButton: true,
    });
  }

  // Danger
  danger() {
    Swal.fire({
      type: 'error',
      title: 'Danger',
      text: 'You clicked the button!',
      showConfirmButton: true,
    });
  }

  // Custom HTML description and buttons with ARIA labels
  customHTML() {
    Swal.fire({
      title: '<strong>HTML <u>example</u></strong>',
      type: 'info',
      html:
        'You can use <b>bold text</b>, ' +
        '<a href="//github.com">links</a> ' +
        'and other HTML tags',
      showCloseButton: true,
      showCancelButton: true,
      focusConfirm: false,
      confirmButtonText:
        '<i class="fa fa-thumbs-up"></i> Great!',
      confirmButtonAriaLabel: 'Thumbs up, great!',
      cancelButtonText:
        '<i class="fa fa-thumbs-down"></i>',
      cancelButtonAriaLabel: 'Thumbs down',
    })
  }

  // custom position
  customPosition() {
    Swal.fire({
      position: 'top-end',
      type: 'success',
      title: 'Your work has been saved',
      showConfirmButton: false,
      timer: 1500
    })
  }

  // Custom animation
  customAnimation() {
    Swal.fire({
      title: 'Custom animation with Animate.css',
      animation: true,
      customClass: 'animated tada'
    });
  }

  // A Custom animation
  customAlert() {
    Swal.fire({
      title: 'Custom width, padding, background.',
      width: 600,
      padding: 100,
      background: '#fff url(coming-soon-bg.jpg)',
      backdrop: `
            rgba(20, 72, 72, 0.48)
            center left
            no-repeat
          `,
      customClass: 'text-dark'
    });
  }

  // A warning message, with a function attached to the "Confirm"-button...
  withConfirmation() {
    Swal.fire({
      title: 'Are you sure?',
      text: "You won't be able to revert this!",
      type: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Yes, delete it!'
    }).then((result) => {
      if (result.value) {
        Swal.fire(
          'Deleted!',
          'Your file has been deleted.',
          'success'
        )
      }
    })
  }

  // A warning message, with a function attached to the "Confirm"-button and by passing a parameter, you can execute something else for "Cancel".
  withCancelled() {
    const swalWithBootstrapButtons = Swal.mixin({
      customClass: {
        confirmButton: 'btn btn-success',
        cancelButton: 'btn btn-danger'
      },
      buttonsStyling: false,
    })
    swalWithBootstrapButtons.fire({
      title: 'Are you sure?',
      text: "You won't be able to revert this!",
      type: 'warning',
      showCancelButton: true,
      confirmButtonText: 'Yes, delete it!',
      cancelButtonText: 'No, cancel!',
      reverseButtons: true
    }).then((result) => {
      if (result.value) {
        swalWithBootstrapButtons.fire(
          'Deleted!',
          'Your file has been deleted.',
          'success'
        )
      } else if (
        // Read more about handling dismissals
        result.dismiss === Swal.DismissReason.cancel
      ) {
        swalWithBootstrapButtons.fire(
          'Cancelled',
          'Your imaginary file is safe :)',
          'error'
        )
      }
    })
  }

  // A message with auto close timer
  autoClose() {
    let timerInterval
    Swal.fire({
      title: 'Auto close alert!',
      html: 'I will close in <strong></strong> seconds.',
      timer: 2000,
      onBeforeOpen: () => {
        Swal.showLoading()
        timerInterval = setInterval(() => {
          Swal.getContent().querySelector('strong')
            .textContent = Swal.getTimerLeft()
        }, 100)
      },
      onClose: () => {
        clearInterval(timerInterval)
      }
    }).then((result) => {
      if (
        // Read more about handling dismissals
        result.dismiss === Swal.DismissReason.timer
      ) {
      }
    })
  }

  // A Ajax request example
  withAjax() {
    Swal.fire({
      title: 'Submit your Github username',
      input: 'text',
      inputAttributes: {
        autocapitalize: 'off'
      },
      showCancelButton: true,
      confirmButtonText: 'Look up',
      showLoaderOnConfirm: true,
      preConfirm: (login) => {
        return fetch(`//api.github.com/users/${login}`)
          .then(response => {
            if (!response.ok) {
              throw new Error(response.statusText)
            }
            return response.json()
          })
          .catch(error => {
            Swal.showValidationMessage(
              `Request failed: ${error}`
            )
          })
      },
      allowOutsideClick: () => !Swal.isLoading()
    }).then((result) => {
      if (result.value) {
        Swal.fire({
          title: `${result.value.login}'s avatar`,
          imageUrl: result.value.avatar_url
        })
      }
    })
  }

  // Chaining modals (queue) example
  stepsAlert() {
    Swal.mixin({
      input: 'text',
      confirmButtonText: 'Next &rarr;',
      showCancelButton: true,
      progressSteps: ['1', '2', '3']
    }).queue([
      {
        title: 'Question 1',
        text: 'Chaining swal2 modals is easy'
      },
      'Question 2',
      'Question 3'
    ]).then((result) => {
