import { Component, Input, OnInit, TemplateRef, ViewChild } from "@angular/core";
import { NavigationEnd, Router } from "@angular/router";
import { NgbModal, ModalDismissReasons } from "@ng-bootstrap/ng-bootstrap";

@Component({
  selector: "app-quick-view",
  templateUrl: "./quick-view.component.html",
  styleUrls: ["./quick-view.component.scss"],
})
export class QuickViewComponent implements OnInit {
  public counter: number = 1;

  @Input() productDetail: any;

  constructor(private router: Router, private ngb: NgbModal) {
    this.router.events.subscribe((event) => {
      if (event instanceof NavigationEnd) {
        this.ngb.dismissAll();
      }
    });
  }

  ngOnInit(): void {}

  public increment() {
    this.counter += 1;
  }

  public decrement() {
    if (this.counter > 1) {
      this.counter -= 1;
    }
  }
}
<div class="modal-header product-modal">
  <div class="product-box row">
    <div class="product-img col-lg-6"><img class="img-fluid" src="{{ productDetail?.img }}" alt="" /></div>
    <div class="product-details col-lg-6 text-start">
      <h4>{{ productDetail?.name }}</h4>
      <div class="product-price">
        ${{ productDetail?.price }}
        <del>${{ productDetail?.discountPrice }}</del>
      </div>
      <div class="product-view">
        <h6 class="f-w-600">Product Details</h6>
        <p class="mb-0">{{ productDetail?.discription }}</p>
      </div>
      <div class="product-size">
        <ul>
          <li>
            <button class="btn btn-outline-light" type="button">M</button>
          </li>
          <li>
            <button class="btn btn-outline-light" type="button">L</button>
          </li>
          <li>
            <button class="btn btn-outline-light" type="button">Xl</button>
          </li>
        </ul>
      </div>
      <div class="product-qnty">
        <h6 class="f-w-600">Quantity</h6>
        <div class="input-group m-b-20">
          <button class="btn btn-primary btn-square bootstrap-touchspin-down" (click)="decrement()">
            <i class="fa fa-minus btnGtr1"></i>
          </button>
          <input class="touchspin text-center form-control" #qty name="quantity" type="text" value="{{ counter }}" />
          <button class="btn btn-primary btn-square bootstrap-touchspin-up" (click)="increment()">
            <i class="fa fa-plus btnLess1"></i>
          </button>
        </div>
        <div class="addcart-btn"><a class="btn btn-primary me-3" [routerLink]="['/ecommerce/cart']">Add to Cart</a><a class="btn btn-primary" [routerLink]="['/ecommerce/product-details']">View Details</a></div>
      </div>
    </div>
  </div>
</div>
